$.validator.addMethod('pattern', function (value, element, param) {
    if (this.optional(element)) {
        return true;
    }
    if (typeof param === 'string') {
        param = new RegExp('^(?:' + param + ")$");
    }
    return param.test(value);
}, 'Invalid format.');

$.validator.addMethod('func', function (value, element, func) {
    if (this.optional(element)) {
        return true;
    }
    return func();
}, 'Invalid format.');